mapper.delete:
DELETE FROM content , file , comment
USING contents content , files file , comments comment
WHERE content.contentId=#{id} AND file.contentIdHaveFile=#{id}
  AND comment.commentedContentId=#{id}

mapper.select:
SELECT contentId,contentCategoryId,
viewCount,content,nickname,title,
submitDate,updateDate,
(SELECT IF ( COUNT(*)>0 , true , false ) FROM files
WHERE files.contentIdHaveFile= contents.contentId) AS fileExistence
FROM contents
WHERE submitDate BETWEEN #{start} AND #{end}

     AND title LIKE '%'||#{keyword}||'%'


     AND contentCategoryId=#{contentCategoryId}

 LIMIT #{page},10;

SELECT COUNT(*) AS count
FROM contents
WHERE submitDate BETWEEN #{start} AND #{end}

    AND title LIKE '%'||#{keyword}||'%'


    AND contentCategoryId=#{contentCategoryId}

SELECT password
FROM contents
WHERE contentId=#{id}

mapper.update:
UPDATE contents
SET viewCount=#{viewCount}
WHERE contentId=#{contentId}

mapper.select:
SELECT contentId
FROM contents
WHERE contentCategoryId=#{contentCategoryId}
    AND title=#{title}
    AND nickname=#{nickname}
    AND content=#{content}
