mapper.insert:
INSERT INTO files(fileId , fileName , fileOriginalName , filePath ,
contentIdHaveFile , fileVolume , fileExtension)
VALUES

    #{item.fileId} , #{item.fileName} , #{item.fileOriginalName} , #{item.filePath} ,
    #{item.fileVolume} ,#{item.fileExtension} , #{item.contentIdHaveFile}

;

mapper.select:
SELECT fileName,fileOriginalName,filePath,fileId
FROM files
WHERE contentIdHaveFile=#{id}
